catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	try{

		//get company sys_id
		var user = g_form.getValue('leaver');

		//get company name from sys_id
		var data = new GlideAjax('SISK_HR_Contract');
		data.addParam('sysparm_name', 'getUserDetails');
		data.addParam('sysparm_id', user);
		data.getXML(setUserDetails);
	}
	catch(e){
		alert(e);
	}


}

function setUserDetails(response){
	//set the currency based on the company
	var answer = response.responseXML.documentElement.getAttribute("answer");
	var data = answer.split('|');
	//g_form.setVisible('email', true);
	g_form.setValue('company', data[0]);
	g_form.setValue('region_division', data[1]);
	g_form.setValue('email', data[2]);
	//g_form.setVisible('email', false);
}
