sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	try{
		//get company sys_id
		var company = g_form.getValue('company');

		//get company name from sys_id
		var ctry = new GlideAjax('SISK_HR_Contract');
		ctry.addParam('sysparm_name', 'getCountry');
		ctry.addParam('sysparm_cat', company);
		ctry.getXML(setCurrency);
	}
	catch(e){
		alert(e);
	}
}

function setCurrency(response){
	//set the currency based on the company
	var answer = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue('curreny', '');
	if(answer != ''){
		var country = '';
		if (answer == 'Ireland'){
			g_form.setValue('currency', 'EUR');
		}

		if (answer == 'United Kingdom'){
			g_form.setValue('currency', 'GBP');
		}
	}
}
