sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		setDisplay(oldValue);
		return;
	}
	setDisplay(newValue);
}

function setDisplay(value){
	g_form.setValue('ipad_required', false);
	g_form.setDisplay('ipad_required', false);
	g_form.setDisplay('ipad_model', false);

	var ga = new GlideAjax('SISK_HR_Contract');
	ga.addParam('sysparm_name', 'getTabletCount');
	ga.addParam('sysparm_value', value);
	ga.getXML(TabletCount);
}

function TabletCount(response){
	var count = response.responseXML.documentElement.getAttribute("answer");

	if (count > 0){
		g_form.setDisplay('ipad_required', true);
		if(count > 1){
			g_form.setDisplay('ipad_model', true);
		}
	}
}
