sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		setDisplay(newValue);
		return;
	}

	//Type appropriate comment here, and begin script below
	setDisplay(newValue);
}

function setDisplay(newValue){
	g_form.setDisplay('reuse_phone', false);
	g_form.setDisplay('sim_type', false);
	g_form.setDisplay('mobile_international_calls', false);
	g_form.setDisplay('mobile_data_roaming', false);
	g_form.setDisplay('existing_phone_details', false);

	if (newValue == 'standard'){
		g_form.setDisplay('reuse_phone', true);
		g_form.setDisplay('sim_type', true);
		g_form.setDisplay('mobile_international_calls',true);
		g_form.setDisplay('mobile_data_roaming',true);
	}
	if (newValue == 'existing'){
		g_form.setDisplay('existing_phone_details',true);
	}
}
