sys_script_client.script:
function onLoad() {
	//get the sys_id's of the relevant fields
	var dept = g_form.getValue('department');
	var grade = g_form.getValue('grade');
	var st = g_form.getValue('state');
	var ph = g_form.getValue('mobile_phone_required');

	//get the id's of the approvers
	var ldir = g_form.getValue('local_director');
	var dir = g_form.getValue('director_name');
	var fdir = g_form.getValue('finance_director');

	//get the id's of the current user
	var curUserID = g_user.userID;


	//hide choice fields
	g_form.setMandatory('job_title_text',false);
	g_form.setMandatory('project',false);

	g_form.setDisplay('job_title_text',false);
	g_form.setDisplay('project',false);
	g_form.setDisplay('allowance', false);
	g_form.setDisplay('mobile_international_calls', false);
	g_form.setDisplay('mobile_data_roaming', false);

	//hide admin and activies
	if(!g_user.hasRoles() || st == '-5'){
		g_form.setSectionDisplay('hr_details', false);
		g_form.setSectionDisplay('activities', false);
	}

	//display the international calls and roaming fields if relevant choice selected
	if (ph == 'true'){
		g_form.setDisplay('mobile_international_calls',true);
		g_form.setDisplay('mobile_data_roaming',true);
	}

	//make salary fields editable for approvers when approval at their stage
	if (st != -5 && st != -6){
		g_form.setReadOnly('director',true);
		g_form.setReadOnly('local_director',true);
		g_form.setReadonly('basic_salary',true);
		g_form.setReadOnly('grade',true);
		g_form.setReadOnly('allowance',true);

		if((st == -9 && ldir == curUserID) || (st == -10 && dir == curUserID) || (st == -11 && fdir == curUserID)){
			g_form.setReadonly('basic_salary',false);
			g_form.setReadOnly('grade',false);
			g_form.setReadOnly('allowance',false);
		}
	}

	//get the department name from the sys_id
	var job = new GlideAjax('SISK_HR_Contract');
	job.addParam('sysparm_name', 'getJobTitleType');
	job.addParam('sysparm_cat', dept);
	job.getXML(jobInput);

	//display the allowance field if relevant choice selected
	if (grade.match(/^car_all.*$/)){
		g_form.setDisplay('allowance', true);
	}
}

function jobInput(response) {
	//set the jobtitle and project fields
	var answer = response.responseXML.documentElement.getAttribute("answer");
	if(answer == 'both' || answer == 'freetext'){
		//replace choice job title with free text
		g_form.setMandatory('job_title',false);
		g_form.setDisplay('job_title',false);

		g_form.setDisplay('job_title_text',true);
		g_form.setMandatory('job_title_text',true);
	}
	if(answer == 'both' || answer == 'project'){
		//set project filed
		g_form.setDisplay('project',true);
		g_form.setMandatory('Project',true);
	}

}
