		g_form.setMandatory('project', false);
		g_form.setDisplay('project', false);
	}*/

	function setValue(value){
		if(value == 'false'){
		//set project filed
		g_form.setDisplay('project',true);
		g_form.setMandatory('project',true);
	} else {
		g_form.setMandatory('project', false);
		g_form.setDisplay('project', false);
	}

	}

}
