sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		setDisplay(newValue);
		return;
	}

	//Display shift fields if 'Contract Type' is part-time
	setDisplay(newValue);
}

function setDisplay(newValue){
	g_form.setDisplay('shift_pattern', false);
	g_form.setDisplay('shift_m', false);
	g_form.setDisplay('shift_tu', false);
	g_form.setDisplay('shift_w', false);
	g_form.setDisplay('shift_th', false);
	g_form.setDisplay('shift_f', false);

	if (newValue == 'part-time'){
		g_form.setDisplay('shift_pattern', true);
		g_form.setDisplay('shift_m', true);
		g_form.setDisplay('shift_tu', true);
		g_form.setDisplay('shift_w', true);
		g_form.setDisplay('shift_th', true);
		g_form.setDisplay('shift_f', true);
	}	
}
