sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
		getValue(oldValue);
        return;
    }

    //Type appropriate comment here, and begin script below
    getValue(newValue);

}

function getValue(value) {
    var ga = new GlideAjax('SISK_HR_Contract');
    ga.addParam('sysparm_name', 'getInterviewQuestions');
    ga.addParam('sysparm_id', value);
    ga.getXML(getQuestions);

    function getQuestions(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');
        g_form.setValue('competency_question_1', answer);
    }

}
