sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var art = parseInt(g_form.getValue('is_articulate_score'));
	var roleq1 = parseInt(g_form.getValue('role_specific_q1_score'));
	var roleq2 = parseInt(g_form.getValue('role_specific_q2_score'));
	var tech = parseInt(g_form.getValue('technology_experience_score'));
	var value = parseInt(g_form.getValue('sisk_values_score'));
	var att = parseInt(g_form.getValue('energy_and_attitude_score'));
	var comp1 = parseInt(g_form.getValue('competency_score_1'));
	var comp2 = parseInt(g_form.getValue('competency_score_2'));
	var comp3 = parseInt(g_form.getValue('competency_score_3'));
	g_form.setValue('total_score', (art + roleq1 + roleq2 + tech + value + att + comp1 + comp2 + comp3) / .45);

   //Type appropriate comment here, and begin script below

}
