sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		setDisplay(oldValue);
		return;
	}

	//Type appropriate comment here, and begin script below
	setDisplay(newValue);
}

function setDisplay(newValue){
	g_form.setMandatory('agency_name', false);
		g_form.setMandatory('recruitment_cost', false);
		g_form.setMandatory('recruitment_currency', false);
		g_form.setMandatory('referral_name', false);
		g_form.setDisplay('agency_name', false);
		g_form.setDisplay('recruitment_cost', false);
		g_form.setDisplay('recruitment_currency', false);
		g_form.setDisplay('referral_name', false);

	if(newValue == 'agency'){	
		g_form.setDisplay('agency_name', true);
		g_form.setDisplay('recruitment_cost', true);
		g_form.setDisplay('recruitment_currency', true);
		g_form.setMandatory('agency_name', true);
		g_form.setMandatory('recruitment_cost', true);
		g_form.setMandatory('recruitment_currency', true);
	}
	if(newValue == 'referral'){	
		g_form.setDisplay('referral_name', true);
		g_form.setMandatory('referral_name', true);
	}
}
