sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		setDisplay(oldValue);
		return;
	}

	//Display 'Contact End Date' field if 'Contract Category' is part-time
	setDisplay(newValue);
}

function setDisplay(newValue){
	g_form.setMandatory('contract_end_date', false);
	g_form.setDisplay('contract_end_date', false);

	if (newValue == 'fixed'){
		g_form.setDisplay('contract_end_date', true);
		var state = g_form.getValue('state');
		if(state == '1' ){
			g_form.setMandatory('contract_end_date', true);
		} else {
			g_form.setMandatory('contract_end_date', false);
		}
	}


}
