sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		setDisplay(oldValue);
		return;
	}
	setDisplay(newValue);
}

function setDisplay(value){
	g_form.setValue('email', false);
	g_form.setDisplay('email', false);
	g_form.setDisplay('personal_data_share', false);
	g_form.setDisplay('remote_access', false);
	g_form.setDisplay('shared_drive_access', false);
	g_form.setDisplay('printer_access', false);
	g_form.setSectionDisplay('it_equipment', false);

	if (value == 'it_access'){
		g_form.setDisplay('email', true);
		g_form.setDisplay('personal_data_share', true);
		g_form.setDisplay('remote_access', true);
		g_form.setDisplay('shared_drive_access', true);
		g_form.setDisplay('printer_access', true);
		g_form.setSectionDisplay('it_equipment', true);
		g_form.setValue('email', true);
		g_form.setReadonly('email', true);
	}
}
