sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
/* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
/* Optional GlideRecord */ event) {
	var calcAL = current.calculate_al_balance;
	template.print('<table>');
	if(calcAL){
		template.print('<tr><td class="field-label">HR to Calculate Annual Leave Balance:</td><td class="form-control">Yes</td></tr>');
	} else {
		template.print('<tr><td class="field-label">HR to Calculate Annual Leave Balance:</td><td class="form-control">No</td></tr>');
		template.print('<tr><td class="field-label">Holiday Balance:</td><td class="form-control">' + current.holiday_balance + '</td></tr>');
		template.print('<tr><td class="field-label">Balance Type:</td><td class="form-control">' + current.holiday_balance_type + '</td></tr>');
	}
	template.print('</table>');

})(current, template, email, email_action, event);
