sys_script_email.script:
var item = new GlideRecord("x_sigr_sisk_hr_co_authority_to_offer");
  item.addQuery("request", current.sysapproval);
  item.query();
  while(item.next()) {
    template.print("<table><tr><td>Name:</td><td>" + item.first_name + " " + item.last_name + "</td></tr>");
    template.print("<tr><td>Company:</td><td>" + item.company + "</td></tr>");
    template.print("<tr><td>Business Unit:</td><td>" + item.business_unit + "</td></tr>");
    template.print("<tr><td>Salary:</td><td>" + item.currency + " " +  item.basic_salary + "</tr>");
    template.print("<tr><td>Grade:</td><td>" + item.grade + "</tr>");
    if(item.currency == 'EUR') {
        template.print("<tr><td>Allowance:</td><td>" + item.currency + " " +  item.allowance + "</tr>");
    }
    template.print("</table>");
  }
