sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
/* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
/* Optional GlideRecord */ event) {
	if(current.company.country == 'United Kingdom'){
		var notice_period = current.notice_period_worked;
		template.print('<table>');
		if(notice_period){
			template.print('<tr><td class="field-label">Notice Period Worked:</td><td class="form-control">Yes</td></tr>');
		} else {
			template.print('<tr><td class="field-label">Notice Period Worked:</td><td class="form-control">No</td></tr>');
			var paid = current.paid_in_lieu;
			if(paid){
				template.print('<tr><td class="field-label">Paid In Lieu:</td><td class="form-control">Yes</td></tr>');
				template.print('<tr><td class="field-label">Paid In Lieu:</td><td class="form-control"> ' + current.paid_in_lieu_details + '</td></tr>');
			} else{
				template.print('<tr><td class="field-label">Paid In Lieu:</td><td class="form-control">No</td></tr>');
			}
		}
		template.print('</table>');
	}

})(current, template, email, email_action, event);
