sys_script_email.script:
var item = new GlideRecord("x_sigr_sisk_hr_co_authority_to_offer");
item.addQuery("sys_id", current.sysapproval);
item.query();
while(item.next()) {
	template.print('<table style="border: 1px solid #707070; padding: 3px 7px 2px 7px; background-color: #d1d1d1;">');
	template.print('<tr><td>Name:</td><td>' + item.first_name + ' ' + item.surname + '</td></tr>');
	template.print('<tr><td>Company:</td><td>' + item.company.name + '</td></tr>');
	template.print('<tr><td>Region/Division:</td><td>' + item.region_division.name + '</td></tr>');
	template.print('<tr><td>Department:</td><td>' + item.department.name + '</td></tr>');
	if(item.project != ''){
		template.print('<tr><td>Project:</td><td>' + item.project.name + '</td></tr>');
	}
	template.print('<tr><td>Job Title:</td><td>' + item.job_title_text + '</td></tr>');
	template.print('<tr><td>Basic Salary:</td><td>' + item.currency + " " + item.basic_salary + '</td></tr>');
	template.print('<tr><td>Grade:</td><td>' + item.grade.getDisplayValue() + '</td></tr>');
	if(item.grade.match(/^car_all.*$/)){
		template.print('<tr><td>Car Allowance:</td><td>' + item.currency + ' ' + item.allowance + '</td></tr>');
	}
	template.print('<tr><td>Reporting Manager:</td><td>' + item.reporting_manager.name + '</td></tr>');
	if(item.local_director != ''){
		template.print('<tr><td>Regional Director:</td><td>' + item.local_director.name + '</td></tr>');
	}
	template.print('</table>');
}
