sys_ui_action.script:
doClone();

function doClone() {
    var clone = new GlideRecord('x_sigr_sisk_hr_co_interview');
    clone.initialize();

    clone.candidate_name = current.candidate_name;
    clone.position = current.position;
    clone.location = current.location;
    clone.recruitment_type = current.recruitment_type;
    clone.candidate_home_location = current.candidate_home_location;
    clone.willing_to_travel = current.willing_to_travel;
    clone.immigration_visa_status = current.immigration_visa_status;
    clone.driving_licence = current.driving_licence;
    clone.cscs_smsts_ssst_safepass_status_expiry_date = current.cscs_smsts_ssst_safepass_status_expiry_date;
    clone.salary_package_expectations = current.salary_package_expectations;
    clone.notice_period_availability = current.notice_period_availability;
    clone.reference_1_details_confirm_complete_and_outcome = current.reference_1_details_confirm_complete_and_outcome;
    clone.reference_2_details_confirm_complete_and_outcome = current.reference_2_details_confirm_complete_and_outcome;

    clone.insert();
	gs.addInfoMessage("Interview Form: " + clone.number + " Created");

    action.setRedirectURL(clone);
    action.setReturnURL(current);
}
