sys_ui_action.script:
var uam = new GlideRecord('u_user_access');
uam.initialize();
uam.u_ato = current.sys_id; //reference the ato in the uam form. This will mean that the UAM request is auto approved
uam.u_first_name = current.first_name;
uam.u_surname = current.surname;
uam.company = current.company;
uam.u_business_unit = current.region_division;
uam.u_job_description = current.job_title_text;
uam.u_start_date = current.start_date_confirmed;
uam.u_employment_type = 'employee';
uam.u_reporting_manager = current.reporting_manager;
uam.u_hrpro_number = current.hrpro_number;
uam.u_hrpro_level = current.hrpro_level;
uam.u_job_number = current.project.job_number;

uam.u_email = current.email;
uam.u_personal_data_share = current.personal_data_share;
uam.u_vpn_access = current.remote_access;
uam.u_shared_drive_access = current.shared_drive_access;
uam.u_printer_access = current.printer_access;
uam.u_shared_drives = current.list_shared_drives;
uam.u_printers_required = current.list_printers;

uam.u_computer = current.computer_type;
uam.u_using_existing_computer = current.existing_computer_details;
uam.u_docking_station = current.docking_station;
uam.u_keyboard = current.keyboard;
uam.u_mouse = current.mouse;
uam.u_monitor = current.monitor;
uam.u_monitor_qty = current.monitor_qty;

uam.u_phone = current.phone_type;
uam.u_use_existing_phone = current.existing_phone_details;
uam.u_sim_type = current.sim_type;
uam.u_roaming = current.mobile_data_roaming;
uam.u_international_calls = current.mobile_international_calls;

current.uam_form = uam.insert();
current.update();
gs.info('UAM ' + current.uam_form.number + 'Created');
action.setRedirectURL(current);
