sys_ui_action.script:
//Client-side 'onclick' function
function submitToIT(){
	var email = g_form.getValue('email');
	var employeeNumber =  g_form.getValue('hrpro_number');
	var employeeLevel =  g_form.getValue('hrpro_level');
	var startDate =  g_form.getValue('start_date_confirmed');

	g_form.hideFieldMsg('email');
	g_form.hideFieldMsg('hrpro_number');
	g_form.hideFieldMsg('hrpro_level');
	g_form.hideFieldMsg('start_date_confirmed');

	if(!email || employeeNumber == '' || employeeLevel == '' || startDate == ''){
		if(!email){	
			g_form.setMandatory('email', true);
			g_form.showFieldMsg('email','Email needs to be selected if user requires IT Access','error');
		}
		if(employeeNumber == ''){	
			g_form.setMandatory('hrpro_number', true);
			g_form.showFieldMsg('hrpro_number','Employee Number is required.','error');
		}
		if(employeeLevel == ''){	
			g_form.setMandatory('hrpro_level', true);
			g_form.showFieldMsg('hrpro_level','Employee Level is required.','error');
		}
		if(startDate == ''){	
			g_form.setMandatory('start_date_confirmed', true);
			g_form.showFieldMsg('start_date_confirmed','Confirmed Start Date is required for IT.','error');
		}
		return false;  //Abort submission
	}
	//Call the UI Action and skip the 'onclick' function
	gsftSubmit(null, g_form.getFormElement(), 'create_it_request'); //MUST call the 'Action name' set in this UI Action
}

//Code that runs without 'onclick'
//Ensure call to server-side function with no browser errors
if(typeof window == 'undefined'){
	createITRequest();
}

function createITRequest(){
	var uam = new GlideRecord('u_user_access');
	uam.initialize();
	uam.u_ato = current.sys_id; //reference the ato in the uam form. This will mean that the UAM request is auto approved
	uam.u_first_name = current.first_name;
	uam.u_surname = current.surname;
	uam.company = current.company;
	uam.u_business_unit = current.region_division;
	uam.u_department = current.department;
	uam.u_role_group = current.job_title.role_group;
	uam.u_job_description = current.job_title_text;
	uam.u_start_date = current.start_date_confirmed;
	uam.u_end_date = current.contract_end_date;
	uam.u_employment_type = 'employee';
	uam.u_reporting_manager = current.reporting_manager;
	uam.u_hrpro_number = current.hrpro_number;
	uam.u_hrpro_level = current.hrpro_level;
	uam.u_job_number = current.project.job_number;

	uam.u_email = current.email;
	uam.u_personal_data_share = current.personal_data_share;
	uam.u_vpn_access = current.remote_access;
	uam.u_shared_drive_access = current.shared_drive_access;
	uam.u_printer_access = current.printer_access;
	uam.u_shared_drives = current.list_shared_drives;
	uam.u_printers_required = current.list_printers;

	var bundle = current.role_package;
	if(bundle == ''){
		bundle = 'standard';
	}
	uam.u_job_role_package = bundle;
	uam.u_computer = current.computer_type;
	uam.u_reuse_computer = current.reuse_computer;

	uam.u_docking_station = current.docking_station;
	uam.u_keyboard = current.keyboard;
	uam.u_mouse = current.mouse;
	uam.u_headset = current.headset;
	uam.u_monitor = current.monitor;
	uam.u_monitor_qty = current.monitor_qty;

	uam.u_ipad_required = current.ipad_required;
	uam.u_phone = current.phone_type;
	uam.u_reuse_phone = current.reuse_phone;
	uam.u_sim_type = current.sim_type;
	uam.u_roaming = current.mobile_data_roaming;
	uam.u_international_calls = current.mobile_international_calls;

	uam.location = current.delivery_location;
	uam.u_delivery_contact_number = current.delivery_contact_number;
	uam.u_delivery_address = current.delivery_address;
	uam.u_additional_details = current.existing_computer_details;

	current.uam_form = uam.insert();
	current.update();
	gs.info('UAM ' + current.uam_form.number + 'Created');
	action.setRedirectURL(current);
}
