answer = ifScript();

function ifScript() {
	
	//Excelerate staff
	if (current.department.name == 'Excelerate Site Staff') {
		return 'yes';
	}
	
	//Basic Salary > 70000 (Ireland)
	if (current.basic_salary > 70000 && current.currency == 'EUR') {
		return 'yes';
	}
	
	//Band ^ or Band 7 (UK)
	if (current.grade == 'band_6' || current.grade == 'band_7') {
		return 'yes';
	}
	return 'no';
}

